<?phpclass MPembahasan extends CI_Model {    function simpanModul($data) {        $this->db->insert('tb_file_pembahasan', $data);    }    function tampilModul($where = null) {        $this->db->select('id,nama,url,kelompok_ujian,upload_time');        $this->db->from('tb_file_pembahasan');        if ($where != null) {            $this->db->where('id',$where);        }        return $this->db->get();    }    function hapusModul($id) {        $this->db->where('id', $id);        $this->db->delete('tb_file_pembahasan');    }}